function Collision({
    rect1,
    rect2
}) {
    return (rect1.attackBox.position.x + rect1.attackBox.width >= rect2.position.x &&
        rect1.attackBox.position.x <= rect2.position.x + rect2.width &&
        rect1.attackBox.position.y + rect1.attackBox.height >= rect2.position.y &&
        rect1.attackBox.position.y <= rect2.position.y + rect2.height &&
        rect1.isAttacking)
}

// create timer
let timer = 121;
let timerID;
// this function will countdown from whatever value timer is set to
function countdown() {
    timerID = setTimeout(countdown, 1000)
    // will run countdown() every second
    if (timer > 0) {
        // subtract timer value by one
        timer--
        // display timer value in the correct place
        document.querySelector('#Inner-Timer').innerHTML = timer;
    }

    // handling what to do when time is up
    if (timer === 0) {
        if (Player.health > Enemy.health) {
            EndResults.innerHTML = 'Player 1 Wins!';
            OuterEnd.style.display = 'block';
        } else if (Player.health < Enemy.health) {
            EndResults.innerHTML = 'Player 2 Wins!';
            OuterEnd.style.display = 'block';
        } else if (Player.health === Enemy.health) {
            EndResults.innerHTML = 'Tie!'
            OuterEnd.style.display = 'block';
        }
    }
}
